		return self::$paymentMapping;
	}

	/**
	 *
	 * @return Customweb_PayEngine3_Container
	 */
	protected final function getContainer(){
		return $this->container;
	}

	public function isB2BSupported(){
		return false;
	}

	public function getOrderId(Customweb_PayEngine3_Authorization_Transaction $transaction){
		$applied = Customweb_Payment_Util::applyOrderSchemaImproved($this->getContainer()->getConfiguration()->getOrderIdSchema(),
				$transaction->getExternalTransactionId(), 30);
		return $applied;
	}

	public function __construct(Customweb_Payment_Authorization_IPaymentMethod $paymentMethod, Customweb_DependencyInjection_IContainer $container){
		parent::__construct($paymentMethod);
		if (!$container instanceof Customweb_PayEngine3_Container) {
			$container = new Customweb_PayEngine3_Container($container);
		}
		$this->container = $container;
	}

	public function useDeviceId(Customweb_Payment_Authorization_IOrderContext $orderContext){
		$use = false;
		switch ($this->getPaymentMethodConfigurationValue('device_id')) {
			case 'inactive':
				return false;
			case 'minimum':
				return $this->getPaymentMethodConfigurationValue('device_id_minimum') < $orderContext->getOrderAmountInDecimals();
			case 'new_customer':
				return $orderContext->isNewCustomer() != 'existing';
			default:
				return false;
		}
	}

	public function processServerAuthorization(Customweb_PayEngine3_Authorization_Transaction $transaction, array $formData){
		$transaction->setFormData($formData);
		$redirect = $this->getContainer()->getAuthorizationHandler()->registerAndExecuteOrder($transaction, $formData);
		if (!empty($redirect)) {
			return $redirect;
		}
		$this->getContainer()->getAuthorizationHandler()->authorizeTransaction($transaction, $formData);
	}

	public function validate(Customweb_Payment_Authorization_IOrderContext $orderContext, Customweb_Payment_Authorization_IPaymentCustomerContext $paymentContext, array $formData){
		$this->preValidate($orderContext, $paymentContext);
	}

	public function useBasket(){
		return false;
	}

	public function getAliasProductName(){
		$params = $this->getPaymentMethodParameters();
		if (isset($params['alias_product'])) {
			return $params['alias_product'];
		}
		return $params['product'];
	}

	public function getProductName(){
		$params = $this->getPaymentMethodParameters();
		return $params['product'];
	}

