	public function getVisibleFormFields(Customweb_Payment_Authorization_IOrderContext $orderContext, $aliasTransaction, $failedTransaction, Customweb_Payment_Authorization_IPaymentCustomerContext $paymentCustomerContext){
		$fields = array();
		if ($this->useDeviceId($orderContext)) {
			$fields[] = $this->getTrackingElement($orderContext, $paymentCustomerContext);
		}
		return $fields;
	}

	public function capture(Customweb_PayEngine3_Authorization_Transaction $transaction, array $items, $close){
		$requestBuilder = new Customweb_PayEngine3_Communication_Transaction_CaptureRequestBuilder($this->getContainer(), $transaction,
				$items);
		$helper = new Customweb_PayEngine3_Communication_Simple($this->getContainer(), $requestBuilder);
		$data = $helper->execute();
		$captureId = $data['transactionId'];
		switch ($data['status']) {
			case 'SUCCESS':
				if (isset($data['order']) && isset($data['order']['paymentInstrument'])) {
					$this->setAliasForDisplay($transaction, $data['order']['paymentInstrument']);
				}
				$captureItem = $transaction->partialCaptureByLineItems($items, $close);
				$captureItem->setExternalId($captureId);
				break;
			case 'OK':
			case 'CREATED':
			case 'PENDING':
			case 'IN PROGRESS':
				$captureItem = $transaction->partialCaptureByLineItems($items, $close,
						Customweb_I18n_Translation::__("The capture was submitted succesfully, but the result is still pending."));
				$captureItem->setExternalId($captureId);
				$captureItem->setStatus(Customweb_Payment_Authorization_ITransactionCapture::STATUS_PENDING);
				$transaction->setUpdateExecutionDate(Customweb_Core_DateTime::_()->addHours(6));
				break;
			case 'EXPIRED':
			case 'ABORTED':
			case 'DECLINED':
			case 'FAILURE':
			default:
				throw new Exception(Customweb_I18n_Translation::__('The capture failed with status !status.', array(
					'!status' => $data['status']
				)));
		}

		$uncaptured = Customweb_Util_Invoice::getTotalAmountIncludingTax($transaction->getUncapturedLineItems());
		if ($close && Customweb_Util_Currency::compareAmount(0, $uncaptured, $transaction->getCurrencyCode()) > 0) {
			try {
				$this->cancel($transaction, false);
			}
			catch (Exception $e) {
				$transaction->addHistoryItem(
						new Customweb_Payment_Authorization_DefaultTransactionHistoryItem(
								Customweb_I18n_Translation::__("Cancelling the rest of the transaction failed with message '@message'.",
										array(
											'@message' => $e->getMessage()
										)), Customweb_Payment_Authorization_ITransactionHistoryItem::ACTION_CAPTURING));
			}
		}
	}

	public function refund(Customweb_PayEngine3_Authorization_Transaction $transaction, array $items, $close){
		$remainingItemsToRefund = $items;
		foreach ($transaction->getCaptures() as $capture) {
			/* @var $capture Customweb_PayEngine3_Authorization_TransactionCapture */
			$refundableItems = $capture->getRefundableItems();

			if (empty($refundableItems)) {
				continue;
			}

			$residualLineItems = Customweb_Util_Invoice::substractLineItems($refundableItems, $remainingItemsToRefund);
			$itemsToRefundForThisCapture = Customweb_Util_Invoice::substractLineItems($refundableItems, $residualLineItems);

			$requestBuilder = new Customweb_PayEngine3_Communication_Transaction_RefundRequestBuilder($this->getContainer(), $transaction,
					$capture, $itemsToRefundForThisCapture);
			$helper = new Customweb_PayEngine3_Communication_Simple($this->getContainer(), $requestBuilder);
			$data = $helper->execute();
			$refundId = $data['transactionId'];
			switch ($data['status']) {
				case 'SUCCESS':
					$refundItem = $transaction->refundByLineItems($itemsToRefundForThisCapture, false);
					$refundItem->setExternalId($refundId);
					$capture->addRefund($refundItem);
					break;
				case 'OK':
				case 'CREATED':
				case 'PENDING':
				case 'IN PROGRESS':
					$refundItem = $transaction->refundByLineItems($itemsToRefundForThisCapture, false,
							Customweb_I18n_Translation::__("The capture was submitted succesfully, but the result is still pending."));
					$refundItem->setExternalId($refundId);
					$refundItem->setStatus(Customweb_Payment_Authorization_ITransactionRefund::STATUS_PENDING);
					$transaction->setUpdateExecutionDate(Customweb_Core_DateTime::_()->addHours(6));
					$capture->addRefund($refundItem);
					break;
				case 'EXPIRED':
				case 'ABORTED':
				case 'DECLINED':
				case 'FAILURE':
				default:
					throw new Exception(
							Customweb_I18n_Translation::__('The capture failed with status !status.', array(
								'!status' => $data['status']
							)));
			}

			$remainingItemsToRefund = Customweb_Util_Invoice::substractLineItems($remainingItemsToRefund, $itemsToRefundForThisCapture);

			// In case we have no remaing items, we stop.
			if (count($remainingItemsToRefund) <= 0) {
				break;
			}
		}
	}

	public function cancel(Customweb_PayEngine3_Authorization_Transaction $transaction, $doCancel = true){
		$requestBuilder = new Customweb_PayEngine3_Communication_Transaction_CancelRequestBuilder($this->getContainer(), $transaction);
		$helper = new Customweb_PayEngine3_Communication_Simple($this->getContainer(), $requestBuilder);
		$data = $helper->execute();
		$cancelId = $data['transactionId'];
		switch ($data['status']) {
			case 'SUCCESS':
				if ($doCancel) {
					$cancelItem = $transaction->cancel();
					$cancelItem->setExternalId($cancelId);
				}
				else {
					$transaction->addHistoryItem(
							new Customweb_Payment_Authorization_DefaultTransactionHistoryItem(
									Customweb_I18n_Translation::__("The remainder of the transaction was cancelled."),
									Customweb_Payment_Authorization_ITransactionHistoryItem::ACTION_CANCELLATION));
				}
				break;
			case 'OK':
			case 'CREATED':
			case 'IN PROGRESS':
				if ($doCancel) {
					$cancelItem = $transaction->cancel(
							Customweb_I18n_Translation::__("The cancel was submitted succesfully, but the result is still pending."));
					$cancelItem->setExternalId($cancelId);
					$cancelItem->setStatus(Customweb_Payment_Authorization_ITransactionCancel::STATUS_PENDING);
					$transaction->setUpdateExecutionDate(Customweb_Core_DateTime::_()->addHours(6));
				}
				else {
					$transaction->addHistoryItem(
							new Customweb_Payment_Authorization_DefaultTransactionHistoryItem(
									Customweb_I18n_Translation::__("The remainder of the transaction was cancelled (in progress)."),
									Customweb_Payment_Authorization_ITransactionHistoryItem::ACTION_CANCELLATION));
				}
				break;
			case 'FAILURE':
			default:
				throw new Exception(Customweb_I18n_Translation::__('The cancel failed with status !status.', array(
					'!status' => $data['status']
				)));
		}
	}

	/**
	 * This method register and executes the transaction to recive the redirection url
	 * 
	 * @param Customweb_PayEngine3_Authorization_Transaction $transaction
	 * @param array $parameters
	 * @throws Customweb_Payment_Exception_PaymentErrorException|Exception
	 * @return string
	 */
	public function generateRedirectUrl(Customweb_PayEngine3_Authorization_Transaction $transaction, array $parameters){
		$handler = new Customweb_PayEngine3_Authorization_Handler($this->getContainer());
		$handler->registerCustomer($transaction, $parameters);
		return $handler->registerAndExecuteOrder($transaction, $parameters);
	}

	public function getAjaxFileUrl(Customweb_Payment_Authorization_ITransaction $transaction){
		return $this->getContainer()->getAssetResolver()->resolveAssetUrl('dummy.js');
	}

	public function setAliasForDisplay(Customweb_PayEngine3_Authorization_Transaction $transaction, array $paymentInstrument){
		if ($transaction->getTransactionContext()->getAlias() == 'new' && isset($paymentInstrument['recurring']) && $paymentInstrument['recurring'] &&
				($paymentInstrument['attributes']) && isset($paymentInstrument['attributes']['email'])) {
			$transaction->setAliasForDisplay($paymentInstrument['attributes']['email']);
		}
	}

	public function isRefundSupported(){
		return !in_array('refund', $this->getNotSupportedFeatures());
	}

	public function isCaptureSupported(){
		return !in_array('capturing', $this->getNotSupportedFeatures());
	}

	public function isCancelSupported(){
		return !in_array('cancellation', $this->getNotSupportedFeatures());
	}
	
	public function getWaitingText(Customweb_Core_Language $language) {
		return Customweb_I18n_Translation::__('Dear customer, it appears to us that your payment was successful, but we are still waiting for confirmation. You can wait on this page and we will redirect you after we received the confirmation. Or you can close this window and we will send out an order confirmation email.')->toString();
	}

	protected function getTrackingElement(Customweb_Payment_Authorization_IOrderContext $orderContext, Customweb_Payment_Authorization_IPaymentCustomerContext $paymentContext){
		$control = new Customweb_Form_Control_HiddenInput('payengine3-tracking');
		$element = new Customweb_Form_HiddenElement($control);
		$element->setJavaScript($this->getTrackingScript($orderContext, $paymentContext, $control->getControlId()));
		return $element;
	}

	protected function getTrackingScript(Customweb_Payment_Authorization_IOrderContext $orderContext, Customweb_Payment_Authorization_IPaymentCustomerContext $paymentContext, $fieldId){
		$script = "
function payengine3ScriptLoaded() {
	return (typeof PayEngine !== 'undefined');
}
function payengine3InitializeDeviceIdent() {
	PayEngine.setPublishableKey('{$this->getContainer()->getConfiguration()->getMerchantId()}');
	PayEngine.initGeneralDeviceIdent(function (error, result) {
		if(error && !(error.cardNumber && error.expiry && error.verification)) {
			if(error.message) {
				alert(error.message);
			} else if (error.error) {
				alert(error.error);
			} else {
				console.dir(error);
				alert('Unkown error occurred.');
			}
		}
		else {
			document.getElementById('$fieldId').value = result.deviceIdentToken;
		}
	});
	{$this->getTrackingJsLoadedCallback($orderContext, $paymentContext)}
}
";
		$script .= Customweb_Util_JavaScript::loadScript($this->getContainer()->getConfiguration()->getBaseJavascriptUrl(),
				'payengine3ScriptLoaded', 'payengine3InitializeDeviceIdent');
		return $script;
	}

	protected function getTrackingJsLoadedCallback(Customweb_Payment_Authorization_IOrderContext $orderContext, Customweb_Payment_Authorization_IPaymentCustomerContext $paymentContext){
		return "";
	}
}
